import React, { useEffect } from 'react';
import gsap from 'gsap';
import { Box, Typography, Button } from '@mui/material';
import { Link } from 'react-router-dom'; // Import Link
import InfoCard from '../components/InfoCard';

function Home() {
  useEffect(() => {
    // Hero section animation
    gsap.from(".hero", { duration: 1, y: -50, opacity: 0 });
    // InfoCard animation
    gsap.from(".info-card", { duration: 1, delay: 0.5, x: -100, opacity: 0 });
  }, []);

  return (
    <Box className="hero" sx={{
      height: '100vh',
      backgroundImage: 'url("https://source.unsplash.com/1600x900/?nature,city")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      color: 'white',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
      textAlign: 'center',
      position: 'relative'
    }}>
      <Typography variant="h2" sx={{ fontWeight: 'bold', fontSize: { xs: '3rem', sm: '4rem', md: '5rem' }, zIndex: 1 }}>
        Hello, I'm [Your Name]
      </Typography>
      <Typography variant="h5" sx={{ zIndex: 1 }}>
        Front-End Developer | React | MUI | JavaScript
      </Typography>
      <Button
        component={Link} // Use Link as the component
        to="/contact" // Set the route to the contact page
        variant="contained"
        color="primary"
        sx={{ zIndex: 1, marginTop: 2 }}
      >
        Contact Me
      </Button>
      <Box className="info-card" sx={{ position: 'absolute', bottom: '20px', zIndex: 1 }}>
        <InfoCard />
      </Box>
    </Box>
  );
}

export default Home;
