import React, { useState } from 'react';
import { TextField, Button, Typography } from '@mui/material';
import sendEmail from '../utils/sendEmail';

function Contact() {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await sendEmail(formData);
    alert("Email Sent Successfully!");
  };

  return (
    <div style={{ marginTop: '2rem' }}>
      <Typography variant="h3" gutterBottom>Contact Me</Typography>
      <form onSubmit={handleSubmit}>
        <TextField fullWidth margin="normal" label="Name" name="name" onChange={handleChange} />
        <TextField fullWidth margin="normal" label="Email" name="email" onChange={handleChange} />
        <TextField fullWidth margin="normal" label="Message" name="message" multiline rows={4} onChange={handleChange} />
        <Button type="submit" variant="contained" color="primary" sx={{ marginTop: 2 }}>Send</Button>
      </form>
    </div>
  );
}

export default Contact;
