import React from 'react';
import { Typography, Box } from '@mui/material';
import { motion } from 'framer-motion';

function About() {
  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        textAlign: 'center',
        marginTop: '2rem',
      }}
    >
      <motion.div
        initial={{ opacity: 0, y: 50 }} // Start hidden and below
        animate={{ opacity: 1, y: 0 }} // Fade in and move up
        transition={{ duration: 0.8, ease: 'easeOut' }} // Smooth animation
      >
        <Typography variant="h3" gutterBottom>About Me</Typography>
      </motion.div>
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, ease: 'easeOut', delay: 0.3 }} // Add delay for staggered effect
      >
        <Typography variant="body1">
          I'm a frontend developer with a knack for animation, clean UI, and solid backend experience.
        </Typography>
      </motion.div>
    </Box>
  );
}

export default About;
