import React, { useState } from 'react';
import { TextField, Button, Typography, Container, Box, Grid } from '@mui/material';
import sendEmail from '../utils/sendEmail';

function Contact() {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [error, setError] = useState({ name: false, email: false, message: false });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value });
    setError({ ...error, [e.target.name]: false }); // Reset error on input change
  };

  const validateForm = () => {
    let isValid = true;
    let tempError = { name: false, email: false, message: false };

    if (!formData.name) {
      tempError.name = true;
      isValid = false;
    }

    if (!formData.email || !/\S+@\S+\.\S+/.test(formData.email)) {
      tempError.email = true;
      isValid = false;
    }

    if (!formData.message) {
      tempError.message = true;
      isValid = false;
    }

    setError(tempError);
    return isValid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return; // Validate form before sending

    try {
      await sendEmail(formData);
      alert('Email Sent Successfully!');
      setFormData({ name: '', email: '', message: '' }); // Clear form after submission
    } catch (err) {
      alert('Error sending email. Please try again.');
    }
  };

  return (
    <Container maxWidth="sm">
      <Box
        sx={{
          marginTop: '4rem',
          padding: '2rem',
          backgroundColor: '#fff',
          borderRadius: '8px',
          boxShadow: 3,
        }}
      >
        <Typography variant="h4" gutterBottom align="center">
          Contact Me
        </Typography>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                error={error.name}
                helperText={error.name && "Name is required"}
                variant="outlined"
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                error={error.email}
                helperText={error.email && "Enter a valid email address"}
                variant="outlined"
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Message"
                name="message"
                value={formData.message}
                onChange={handleChange}
                error={error.message}
                helperText={error.message && "Message is required"}
                variant="outlined"
                multiline
                rows={4}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                sx={{
                  width: '100%',
                  padding: '10px 20px',
                  fontSize: '1.1rem',
                  '&:hover': { backgroundColor: '#1976d2' },
                  boxShadow: 2,
                }}
              >
                Send
              </Button>
            </Grid>
          </Grid>
        </form>
      </Box>
    </Container>
  );
}

export default Contact;
