import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import Hero from '../components/Hero';
import InfoCard from '../components/InfoCard';
import './Home.css'; // Create this CSS file for additional styles

// Register ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

function Home() {
  const sectionRef = useRef(null);
  const infoCardsRef = useRef([]);
  
  // Add ref to array
  const addToRefs = (el) => {
    if (el && !infoCardsRef.current.includes(el)) {
      infoCardsRef.current.push(el);
    }
  };

  useEffect(() => {
    // Hero animation
    gsap.from(".hero", { 
      duration: 1.5, 
      y: -80, 
      opacity: 0,
      ease: "power3.out"
    });
    
    // Background shape animations
    gsap.from(".bg-shape-1", {
      duration: 2,
      x: -200,
      y: -200,
      opacity: 0,
      delay: 0.5,
      ease: "elastic.out(1, 0.5)"
    });
    
    gsap.from(".bg-shape-2", {
      duration: 2,
      x: 200,
      y: 200,
      opacity: 0,
      delay: 0.8,
      ease: "elastic.out(1, 0.5)"
    });
    
    // Info cards staggered animation
    infoCardsRef.current.forEach((card, i) => {
      gsap.from(card, {
        scrollTrigger: {
          trigger: card,
          start: "top 80%",
          toggleActions: "play none none none"
        },
        duration: 0.8,
        x: i % 2 === 0 ? -50 : 50,
        opacity: 0,
        delay: i * 0.2,
        ease: "back.out(1.2)"
      });
    });
    
    // Section reveal animation
    gsap.from(sectionRef.current, {
      scrollTrigger: {
        trigger: sectionRef.current,
        start: "top 80%",
        toggleActions: "play none none none"
      },
      duration: 1,
      y: 50,
      opacity: 0,
      ease: "power2.out"
    });
    
    // Clean up
    return () => {
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
    };
  }, []);

  return (
    <div className="home-container">
      {/* Animated background shapes */}
      <div className="bg-shape bg-shape-1"></div>
      <div className="bg-shape bg-shape-2"></div>
      
      {/* Hero section */}
      <section className="hero-section">
        <div className="hero">
          <Hero />
        </div>
      </section>
      
      {/* Main content */}
      <main className="main-content">
        {/* Info cards grid */}
        <div className="info-cards-grid">
          <div className="info-card" ref={addToRefs}>
            <InfoCard 
              title="My Skills"
              content="Full-stack development, UI/UX design, animation"
              icon="💻"
            />
          </div>
          <div className="info-card" ref={addToRefs}>
            <InfoCard 
              title="Experience"
              content="5+ years building digital products"
              icon="🚀"
            />
          </div>
          <div className="info-card" ref={addToRefs}>
            <InfoCard 
              title="Projects"
              content="Check out my latest work"
              icon="✨"
            />
          </div>
        </div>
        
        {/* About section */}
        <section className="about-section" ref={sectionRef}>
          <h2 className="section-title">Creative Problem Solver</h2>
          <p className="section-text">
            I combine technical expertise with artistic vision to create 
            memorable digital experiences that users love.
          </p>
          <button className="cta-button">
            Explore My Work
          </button>
        </section>
      </main>
    </div>
  );
}

export default Home;
